mapper.update:
UPDATE noti_stng
SET		
	bd_stng_yn =#{bdStngYn}
	, lp_stng_yn =#{lpStngYn}
	, lv_stng_yn =#{lvStngYn}
	, lk_stng_yn =#{lkStngYn}
	, me_stng_yn =#{meStngYn}
WHERE com_profile_no = #{comProfileNo}

mapper.select:
SELECT noti_no, TO_CHAR(noti_dt, 'YYYY-MM-DD HH24:MI:SS') AS noti_dt, noti_sndr_no, noti_rcvr_no, noti_cn, noti_org_no, noti_org_tb, (SELECT board_delyn FROM community_post cp WHERE cp.board_no=n.noti_org_no) as BOARD_DELYN, (SELECT reply_delyn FROM COMMUNITY_REPLY cr WHERE BOARD_NO=cr.BOARD_NO and cr.reply_no = n.noti_org_no) as REPLY_DELYN, (SELECT DISTINCT BOARD_NO FROM COMMUNITY_REPLY CR WHERE CR.REPLY_NO =N.noti_org_no FETCH FIRST 1 ROWS ONLY) AS BOARD_NO, ( SELECT file_save_locate FROM ( SELECT fd.file_save_locate FROM file_detail fd JOIN artist_group a ON fd.file_group_no = a.file_group_no WHERE a.art_group_no = n.noti_sndr_no ORDER BY fd.file_sn DESC ) WHERE ROWNUM = 1 ) AS file_save_locate FROM notification n WHERE 1=1 AND noti_rcvr_no = #{memNo} AND noti_dt >= SYSDATE -3
AND noti_sndr_no = #{artGroupNo}
ORDER BY noti_dt DESC FETCH FIRST 20 ROWS ONLY
