mapper.insert:

	SELECT NVL(MAX(REPLY_NO),0)+1
	FROM COMMUNITY_REPLY

INSERT INTO community_reply (
	  reply_no
	, reply_content
	, reply_delyn
	, reply_create_dt
	, board_no
	, art_no
	, mem_no
	, media_post_no
	, com_profile_no
	, url_category
	)
VALUES (
	  #{replyNo}
	, #{replyContent}
	, 'N'
	, SYSDATE
	, DEFAULT
	, #{artNo}
	, #{memNo}
	, #{mediaPostNo}
	, #{comProfileNo}
	, #{urlCategory}
	)

mapper.select:
SELECT /*com.ohot.home.media.mapper.MediaCommentMapper.getReplyList*/ cr.reply_no ,cr.reply_content ,cr.reply_delyn ,TO_CHAR(cr.reply_create_dt, 'YYYY-MM-DD HH24:MI') as reply_create_dt ,cr.board_no ,cr.art_no ,cr.mem_no ,cr.media_post_no ,cr.com_profile_no ,cr.tk_no ,cr.url_category
/* 프로필 정보 */ ,cp.mem_no ,cp.com_nm ,cp.com_file_group_no ,cp.com_join_ymd ,cp.com_delyn ,cp.com_auth ,cp.art_group_no ,cp.membership_yn
/* 파일 정보 */ ,fd.file_save_locate ,fd.file_save_name FROM community_reply cr INNER JOIN community_profile cp ON cp.com_profile_no = cr.com_profile_no LEFT OUTER JOIN file_group fg ON(cp.com_file_group_no = fg.file_group_no) LEFT OUTER JOIN ( SELECT file_sn , file_group_no , file_original_name , file_save_name , file_save_locate , file_size , file_ext , file_mime , file_fancysize , file_save_date , file_downcount , ROW_NUMBER() OVER (PARTITION BY file_group_no ORDER BY file_sn DESC) AS RN FROM file_detail ) FD ON fg.file_group_no = fd.file_group_no AND FD.RN = 1 WHERE 1=1
AND cp.art_group_no = #{artGroupNo}
AND cr.media_post_no = #{mediaPostNo}
AND cr.reply_delyn = #{replyDelYn}
AND cr.reply_create_dt BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD')
AND cp.com_nm = #{comNm}
ORDER BY cr.reply_create_dt DESC

mapper.update:
UPDATE community_reply
SET reply_content = #{replyContent}
WHERE reply_no = #{replyNo}

UPDATE COMMUNITY_REPLY
SET REPLY_DELYN = 'Y'
WHERE REPLY_NO = #{replyNo}
