mapper.select:
SELECT A.MEDIA_MEBERSHIP_YN, A.MEDIA_VIDEO_URL, A.MEDIA_POST_NO, A.MEDIA_POST_TITLE, A.MEDIA_POST_CN, A.MEDIA_REG_DT, A.FILE_GROUP_NO, A.MEDIA_DEL_YN, A.ISBANNER_YN, A.ART_GROUP_NO, B.ART_GROUP_NM FROM MEDIA_POST A LEFT JOIN ARTIST_GROUP B ON A.ART_GROUP_NO = B.ART_GROUP_NO
AND A.ART_GROUP_NO = #{artGroupNo}
AND B.ART_GROUP_NM = #{artGroupNm}
AND A.MEDIA_REG_DT >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND A.MEDIA_REG_DT <= TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
AND A.MEDIA_MEBERSHIP_YN = #{mediaMebershipYn}
AND A.ISBANNER_YN = #{isbannerYn}
AND A.MEDIA_DEL_YN = #{mediaDelYn}
AND A.MEDIA_POST_TITLE LIKE '%' || #{mediaPostTitle} || '%'
ORDER BY A.MEDIA_REG_DT DESC

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY a.media_reg_dt DESC) AS rnum , a.media_mebership_yn , a.media_video_url , a.media_post_no , a.media_post_title , a.media_post_cn , a.media_reg_dt , TO_CHAR(media_reg_dt, 'YYYY"년" MM"월" DD"일"') AS format_reg_dt , a.file_group_no , a.media_del_yn , a.isbanner_yn , a.art_group_no , b.art_group_nm FROM media_post a LEFT JOIN artist_group b ON a.art_group_no = b.art_group_no
AND a.art_group_no = #{artGroupNo}
AND b.art_group_nm = #{artGroupNm}
AND a.media_reg_dt >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND a.media_reg_dt <= TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1
AND a.media_mebership_yn = #{mediaMebershipYn}
AND a.isbanner_yn = #{isbannerYn}
AND a.media_del_yn = #{mediaDelYn}
AND a.media_post_title LIKE '%' || #{mediaPostTitle} || '%'
) WHERE rnum BETWEEN #{start} AND #{end}

mapper.insert:
		INSERT INTO MEDIA_POST(
                          MEDIA_POST_NO
                        , MEDIA_POST_TITLE
                        , MEDIA_POST_CN
                        , MEDIA_REG_DT
                        , MEDIA_DEL_YN
                        , ISBANNER_YN
                        , ART_GROUP_NO
                        , MEDIA_MEBERSHIP_YN
                        , MEDIA_VIDEO_URL
                        , FILE_GROUP_NO
		)
		VALUES (
		          (SELECT NVL(MAX(MEDIA_POST_NO), 0) + 1 FROM MEDIA_POST)
		        , #{mediaPostTitle}
		        , #{mediaPostCn}
		        , SYSDATE
		        , 'N'
		        , #{isbannerYn}
		        , #{artGroupNo} /*17 newjeans*/
		        , #{mediaMebershipYn}
		        , #{mediaVideoUrl}
		        , #{fileGroupNo}
		)

mapper.update:
	UPDATE  MEDIA_POST
  	   SET  MEDIA_POST_TITLE = #{mediaPostTitle}
	        , MEDIA_POST_CN = #{mediaPostCn}
	        , MEDIA_DEL_YN = 'N'
	        , ISBANNER_YN = #{isbannerYn}
	        , ART_GROUP_NO = #{artGroupNo}
	        , MEDIA_MEBERSHIP_YN = #{mediaMebershipYn}
	        , MEDIA_VIDEO_URL = #{mediaVideoUrl}
	        , FILE_GROUP_NO = #{fileGroupNo}
	 WHERE 1=1
  	   AND  MEDIA_POST_NO = #{mediaPostNo}

mapper.select:
SELECT membership_yn
FROM community_profile
WHERE com_profile_no = #{comProfileNo}

SELECT COUNT(*)
FROM media_post a
LEFT JOIN artist_group b ON a.art_group_no = b.art_group_no


        AND a.art_group_no = #{artGroupNo}


        AND b.art_group_nm = #{artGroupNm}


        AND a.media_reg_dt >= TO_DATE(#{startDate}, 'YYYY-MM-DD')


        AND a.media_reg_dt <= TO_DATE(#{endDate}, 'YYYY-MM-DD') + 1


        AND a.media_mebership_yn = #{mediaMebershipYn}


        AND a.isbanner_yn = #{isbannerYn}


        AND a.media_del_yn = #{mediaDelYn}


        AND a.media_post_title LIKE '%' || #{mediaPostTitle} || '%'
