mapper.select:
SELECT g.gds_no , g.gds_type , g.gds_nm , g.expln , g.comm_code_grp_no , g.art_group_no , g.art_no , g.file_group_no , t.tk_no , t.tk_ctgr , t.tk_lctn , TO_CHAR(TO_DATE(T.TK_START_YMD, 'YYYYMMDD'),'YYYY-MM-DD') AS TK_START_YMD , TO_CHAR(TO_DATE(T.TK_FINISH_YMD, 'YYYYMMDD'),'YYYY-MM-DD') AS TK_FINISH_YMD ,(SELECT tf.file_save_locate FROM file_detail tf WHERE tf.file_group_no = t.poster_file) AS tk_file_save_locate ,(SELECT a.art_group_nm FROM artist_group a WHERE a.art_group_no = g.art_group_no) AS art_group_nm ,(SELECT a.art_act_nm FROM artist a WHERE a.art_no = g.art_no) AS art_act_nm FROM goods g LEFT JOIN ticket t ON g.gds_no = t.gds_no WHERE g.comm_code_grp_no='GD02' AND g.gds_del_yn='N'
AND t.tk_ctgr = #{tkCtgr}
ORDER BY TK_START_YMD DESC

	SELECT COUNT(*) 
	  FROM tk_rsvtn 
	 WHERE 1=1
  	   AND mem_no = #{memNo}
	   AND tk_detail_no = #{tkDetailNo}
	   AND rsvtn_enum =2

		SELECT COUNT(*)
		FROM tk_rsvtn
		WHERE 1=1
		AND tk_detail_no = #{tkDetailNo}
        AND seat_no = #{seatNo}
        AND rsvtn_enum IN (1, 2)

mapper.insert:

		SELECT NVL(MAX(REPLY_NO),0)+1 FROM COMMUNITY_REPLY

INSERT INTO community_reply (
		reply_no
		, reply_content
		, reply_delyn
		, reply_create_dt
		, mem_no
		, tk_no
		, com_profile_no
		, media_post_no
		, url_category
		)
VALUES(
		#{replyNo}
		,#{replyContent}
		,'N'
		,SYSDATE
		,#{memNo}
		,#{tkNo}
		,0
		,0
		,#{urlCategory}
)

mapper.update:
UPDATE COMMUNITY_REPLY
   SET reply_content = #{replyContent}
 WHERE REPLY_NO=#{replyNo}

UPDATE COMMUNITY_REPLY
   SET reply_delyn ='Y'
 WHERE REPLY_NO=#{replyNo}
